<template>  <div class="__cus-tab-bar" id="__cus-tab-bar" >    <div class="__cus-tabs-wrap">      <div class="__cus-tab-bar-item" v-for="item in tabData" :key="item.title">        {{item.title}}      </div>      <div class="__cus-tab-indicator" id="__cus-indicator"></div>    </div>  </div></template><script lang="ts">  import { Component, Prop, Vue } from 'vue-property-decorator';  import TabController from './Controller';  @Component({    name: 'CusTabBar',  })  export default class CusTabBar extends Vue {    @Prop() controller!: TabController;    get tabData() {      return  this.controller?this.controller.data:[]    }  }</script>