<template>  <div class="tab-container" id="__cus-tab-container" :style="this.styles">    <div class="tab-container-items-wrap">      <div class="tab-container-item" v-for="item in tabData">        <div class="list" v-if="item.type==='list'">          <ul>            <li v-for="listItem in item.data">{{listItem.title}}</li>          </ul>        </div>        <div class="video" v-if="item.type==='block'">          <ul>            <li>video</li>          </ul>        </div>      </div>    </div>  </div></template><script lang="ts">  import { Component, Prop, Vue } from 'vue-property-decorator';  import TabController from './Controller';  @Component({    name: 'CusTabContainer',  })  export default class CusTabContainer extends Vue {    @Prop({default:()=>({})}) styles?: object;    @Prop() controller!: TabController;    get tabData(){      return  this.controller?this.controller.data:[]    }  }</script>